mapper.select:
select C.*
from Club C
where C.id = #{param1}

select CM.verify
from Club_Member CM
where CM.club_id = #{param1} and CM.member_id = #{param2}

select C.image_id
from club C
where C.id = #{param1}

select I.*
from image I, club C
where C.image_id = I.id and C.id = #{param1}

SELECT count(*)
from notice N 
where N.club_id=#{param1}

SELECT count(*)
from post p 
where p.club_id=#{param1}

SELECT count(*)
from album a 
where a.club_id=#{param1}

SELECT count(*)
from comment c 
where c.id=#{param1}
